def izpis(source):
    import io, sys
    old_stdout = sys.stdout
    new_stdout = io.StringIO()
    sys.stdout = new_stdout
    exec(source)
    sys.stdout = old_stdout
    return new_stdout.getvalue()

def preveriSmrecico(smrecica, levi, desni, dovoljeni):
    vrstice = smrecica.splitlines()
    if len(vrstice) != 8:
        _warn("Smrečica nima osmih vrstic.")
    for i in range(1, min(8, len(vrstice))):
        vrstica = vrstice[i]
        if not vrstica.startswith((levi[i] - 1) * " "):
            _warn("{0}. vrstica se ne začne na {1}. znaku.".format(i + 1, levi[i]))
        if len(vrstica.rstrip()) != desni[i]:
            _warn("{0}. vrstica se ne konča na {1}. znaku.".format(i + 1, desni[i]))
        znaki = vrstica.replace(" ", "")
        if len(znaki) != i + 1:
            _warn("{0}. vrstica ni sestavljena iz {0} znakov".format(i + 1))
        if any(znak not in dovoljeni for znak in znaki):
            _warn("{0}. vrstica vsebuje nedovoljene znake.".format(i + 1))
    if len([znak for znak in dovoljeni if znak in smrecica]) > 1:
        _warn("Smrečica je sestavljena iz različnih dovoljenih znakov.")

